import React, { useState,useEffect } from "react";
import Card from "./components/card";
import InputField from "./components/inputfield";

const App = () => {
	const [friends, setFriends] = useState([
		{ id: 1, name: "Ruth", email: "ruth@gmail.com" },
		{ id: 2, name: "Rebecca", email: "rebecca@gmail.com" },
		{ id: 3, name: "Daniella", email: "daniella@gmail.com" },
	]);
	let [friendsTampon, setFriendsTampon] = useState(friends);

    useEffect(()=>{ 
        fetch()
    })
	const handleChange = (e) => {
		e.preventDefault();
		const filteredFriends = friends.filter((friend) => {
			return friend.name.toLowerCase().includes(e.target.value.toLowerCase());
		});
		setFriendsTampon((friendsTampon = filteredFriends));
	};

	return (
		<div>
			<h1>MES AMIS ROBOTS</h1>
			<InputField onChange={handleChange} />
			<div>
				{friendsTampon.map((friend, index) => {
					return <Card key={index} name={friend.name} email={friend.email} />;
				})}
			</div>
		</div>
	);
};
export default App;
