import React, { useState, useEffect } from "react";
import Card from "./components/card";
import InputField from "./components/inputfield";
import "./App.css";
import Modal from "react-modal";
import Loading from "./components/loading";

Modal.setAppElement("#root");
const App = () => {
	let [friends, setFriends] = useState([]);
	let [load, setLoad] = useState(false);
	let [modalIsOpen, setModalIsOpen] = useState(false);
	let [singleFriend, setSingleFriend] = useState({});

	let [friendsTampon, setFriendsTampon] = useState(friends);
	useEffect(() => {
		setLoad(true);
		fetch("http://jsonplaceholder.typicode.com/users")
			.then((response) => response.json())
			.then((data) => {
				let dataMapped = data.map(({ id, name, email }) => {
					return { id, name, email };
				});
				setFriends(dataMapped);
				setFriendsTampon(dataMapped);
				setLoad(false);
			});
	}, []);

	const handleChange = (e) => {
		e.preventDefault();
		const filteredFriends = friends.filter((friend) => {
			return friend.name.toLowerCase().includes(e.target.value.toLowerCase());
		});
		setFriendsTampon((friendsTampon = filteredFriends));
	};

	const openModal = (id) => {
		setSingleFriend(
			friends.find((friend) => {
				return friend.id === id;
			})
		);
		setModalIsOpen(true);
	};

	return (
		<div className="App">
			{load ? (
				<Loading />
			) : (
				<>
					<h1 className="bloc-cards">
						<div className="centered">MES</div>{" "}
						<div className="centered">AMIS</div>
						<div className="centered">ROBOTS</div>
					</h1>
					<InputField onChange={handleChange} />
					<div className="bloc-cards">
						{friendsTampon.map((friend, index) => {
							return (
								<Card
									onClick={() => openModal(friend.id)}
									key={index}
									name={friend.name}
									email={friend.email}
									url={`https://robohash.org/${friend.id}`}
								/>
							);
						})}
					</div>
					<Modal
						isOpen={modalIsOpen}
						onRequestClose={() => setModalIsOpen(false)}>
							  style={{
    overlay: {
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      backgroundColor: 'rgba(255, 255, 255, 0.75)'
    },
    content: {
      position: 'absolute',
      top: '40px',
      left: '40px',
      right: '40px',
      bottom: '40px',
      border: '1px solid #ccc',
      background: '#fff',
      overflow: 'auto',
      WebkitOverflowScrolling: 'touch',
      borderRadius: '4px',
      outline: 'none',
      padding: '20px',
	  width: '100px'
    }
  }}
						<div >
							<div>
								<img src={`https://robohash.org/${singleFriend.id}`} alt={singleFriend.name} />
							</div>
							<h2 className="modal-title centered">{singleFriend.name}</h2>
							<h4>{singleFriend.email}</h4>
						</div>
					</Modal>
				</>
			)}
		</div>
	);
};
export default App;
