import React  from 'react';
import Card from './components/card';
import InputField from './components/inputfield';

const App = () => {

    const [friends,setFriends]=useSate([
        {id:1,name:"Ruth",email:"ruth@gmail.com"},
        {id:2,name:"Rebecca",email:"rebecca@gmail.com"}
    ])
    return(
        <div>
            <h1>MES AMIS ROBOTS</h1>
            <InputField />
            <div>
            {friends.map(friend => {
                return (
                    <Card name={friend.name} email={friend.email} />
                )
            })}
            </div>          
        </div>
    )
}
export default App;
